import React from 'react';


class ListProducts extends React.Component {
    constructor(props) {
        super(props);
        this.setState(state => ({
            jobs: []
          }));
       // this.state = {jobs:[]};//using vanilla state as its a simple use case should use redux insted
      }


    componentDidMount() {
        fetch('http://api.dataatwork.org/v1/jobs')//using fetch as its esc default
            .then(res => res.json())
            .then((data) => {
                console.log(JSON.stringify(data))
                this.setState({ jobs: data.splice(0,data.length-1) })
            })
            .catch(console.log);
    }
    render() {
        const items = this.state.jobs;

        return (
            <div>
                <ol>
                    {this.state.jobs.map((value, index) => {
                        return <li key={value.uuid}>{value.title}</li>
                    })}
                </ol>
            </div>
        );
    }
}

export default ListProducts;
