import React from 'react';
const callBackend=require("../../services/CallSpring").callBackend
import { Form,Button } from 'react-bootstrap';

class InvokeBackend extends React.Component {
    constructor(props) {
        super(props);
       this.state = {backendData:{}};//using vanilla state as its a simple use case should use redux insted
      }


    componentDidMount() {
        callBackend()//using fetch as its esc default
            .then(res => res.json())
            .then((data) => {
                console.log(JSON.stringify(data))
                this.setState({ backendData: data})
            })
            .catch(console.log);
    }
    render() {
        const data = this.state.backendData;

        return (
            <div>
                 <Form>
                <Form.Group controlId="formBasicEmail">
                    <Form.Label inline >Email address</Form.Label>
                    <Form.Control inline type="test" placeholder="Job UUID" onChange={e=>this.readTextBoxValue(e)}/>
                    <Button inline variant="primary" onClick={(e)=>this.fireSearchAPI()}>Search</Button>
                </Form.Group>
            </Form>
                <ol>
                  {data.source}
                </ol>
            </div>
        );
    }
}

export default InvokeBackend;
